import React ,{useContext, useEffect}from 'react'
import { ListGroup, ListGroupItem } from 'react-bootstrap'
import './Sidebar.css'
import { AppContext } from '../context/appContext';
import { useSelector } from "react-redux";
function Sidebar() {
  
  const user = useSelector((state) => state.user);
 const {socket,setCurrentRoom,setRooms,rooms,currentRoom} = useContext(AppContext)

useEffect(()=>{
 if(user){
   setCurrentRoom("HTML/CSS");
   getRooms();
   socket.emit("join-room", "General");
   socket.emit("new-user");
 }
},[])

  socket.off("new-user").on("new-user",(payload)=>{
    console.log(payload)
  })
  function getRooms(){
    fetch("http://localhost:5001/rooms")
    .then((res)=>res.json())
    .then((data)=> setRooms(data))
  }

    if (!user) {
      return <></>;
    }

  return (
    <div id="con">
      <br />
      <h2>Avalaible Rooms</h2>
      <ListGroup>
        {/* <ListGroup.Item>
          <i class="fa-brands fa-html5" style={{ color: "red" }}></i>
          {room[0]}
        </ListGroup.Item>
        <ListGroup.Item>{room[1]}</ListGroup.Item>
        <ListGroup.Item>{room[2]}</ListGroup.Item>
        <ListGroup.Item>{room[3]}</ListGroup.Item> */}
        {rooms.map((room,idx)=>(
          <ListGroup.Item key={idx}>{room}</ListGroup.Item>
        ))}
      </ListGroup>
     
    </div>
  );
}

export default Sidebar
