import React ,{useState,useEffect} from 'react'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import './slider.css';
import tuto from "../assets/tuto.jpg";
import forum from "../assets/forum.jpg";
import projet from "../assets/projet.jpg";
import lo from './assets/logonp.JPG'
const images = [
  {
    id: 1,
    image: `${tuto}`,
    title: "Titre 1",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
  {
    id: 2,
    image: `${lo}`,
    title: "Titre 2",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
  {
    id: 3,
    image:`${projet}`,
    title: "Titre 3",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
];

function Slider() {
const [currentIndex, setCurrentIndex] = useState(0);

useEffect(() => {
  const intervalId = setInterval(() => {
    const newIndex = (currentIndex + 1) % images.length;
    setCurrentIndex(newIndex);
  }, 1000); // Change image every 1 second (1000 milliseconds)

  return () => clearInterval(intervalId); // Cleanup function to stop interval on unmount
}, [currentIndex, images.length]);


  return (
    <Carousel
      autoPlay={false}
      infiniteLoop
      showStatus={false}
      showThumbs={false}
      current={currentIndex}
    >
      {images.map((image) => (
        <div key={image.id}>
          <img className="carousel-image" src={image.image} alt='' />
          <div className="legend">
            <h2 className='titlee'>{image.title}</h2>
            <p className='texte'>{image.text}</p>
          </div>
        </div>
      ))}
    </Carousel>
  );
}

export default Slider
