import React, { useContext, useState } from "react";
import {useSelector} from 'react-redux'
import {AppContext} from '../context/appContext'
import {
  Button,
  Col,
  Form,
  FormControl,
  FormGroup,
  Row,
} from "react-bootstrap";
import "./MessageForm.css";

function MessageForm() {

const [message,setMessage] = useState("")
const user = useSelector((state) => state.user);
const { socket,currentRoom,setMessages,messages} = useContext(AppContext)

function getFormattedDate(){
  const date = new Date()
  const year = date.getFullYear()
  let month = (1+ date.getMonth()).toString()
  month = month.length > 1 ? month : "0" + month;
  let day = date.getDate().toString()
  day=day.length > 1? day:"0" + day
  return month + "/" + day + "/" +year
}



  

  const todayDate = getFormattedDate()
function handleSubmit(e) {
  e.preventDefault();
  if (!message) return;
  const today = new Date();
  const minutes =
    today.getMinutes() < 10 ? "0" + today.getMinutes() : today.getMinutes();
  const time = today.getHours() + ":" + minutes;
  const roomId = currentRoom;
  socket.emit("message-room", roomId, message, user, time, todayDate);
  setMessage("");
}

  return (
    <>
      <br />
      <div div className="messages-output">
        {/* {!user && <div className="alert alert-primary">Please Login</div>} */}
        
        {user &&
          messages.length > 0 && // Check for both user and messages array length
          messages.map(({ _id: date, messagesByDate }, idx) => (
            <div key={idx}>
              <p className="alert alert-info text-center message-date-indicator">
                {date}
              </p>
              {messagesByDate?.map(
                ({ content, time, from: sender }, msgIdx) => (
                  <div className="messages" key={msgIdx}>
                    <p>{content}</p>
                  </div>
                )
              )}
            </div>
          ))}
      </div>
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col md={11}>
            <FormGroup>
              <FormControl
                type="text"
                placeholder="Your message"
                disabled={!user}
                value={message}
                onChange={(e) => setMessage(e.target.value)}
              ></FormControl>
            </FormGroup>
          </Col>
          <Col md={1}>
            <Button
              disabled={!user}
              className="btn btn-outline-primary"
              id="btnmess"
              type="submit"
              style={{ width: "100%", backgroundColor: "rgb(0, 212, 255,1)" }}
            >
              <i className="fas fa-paper-plane"></i>
            </Button>
          </Col>
        </Row>
      </Form>
    </>
  );
}

export default MessageForm;
