import React ,{useState} from 'react'
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import './slider.css';
import tuto from "../assets/tuto.jpg";
import forum from "../assets/forum.jpg";
import projet from "../assets/projet.jpg";
import lo from './assets/logonp.JPG'
const images = [
  {
    id: 1,
    image: `${tuto}`,
    title: "Titre 1",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
  {
    id: 2,
    image: `${lo}`,
    title: "Titre 2",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
  {
    id: 3,
    image:`${projet}`,
    title: "Titre 3",
    text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit.",
  },
];

function Slider() {
const [activeSlide, setActiveSlide] = useState(0);

const handleSlideChange = (index) => {
  setActiveSlide(index);
};


  return (
    <Carousel
      autoPlay
      infiniteLoop
      showStatus={false}
      showThumbs={false}
      onChange={handleSlideChange}
    >
      {images.map((image, index) => (
        <div
          key={image.id}
          className={index === activeSlide ? "active-slide" : ""}
        >
          <img className src={image.image} alt={image.title} />
          <div className="legend">
            <h2>{image.title}</h2>
            <p>{image.text}</p>
          </div>
        </div>
      ))}
    </Carousel>
  );
}

export default Slider
